import React, { useState } from 'react';
import './index.css';

const App = () =>{
    const [fullname,setfullname]=useState({
        fname:'',
        lname:'',
        email:'',
        phone:'',
    });

    const Eventelement = (event) =>{

        const {name,value} = event.target;

        setfullname((preValue) =>{
           if(name === 'fname'){
               return{
                   fname : value,
                   lname: preValue.lname,
                   email:preValue.email,
                   phone:preValue.phone,
               };
           }else if(name === 'lname'){
               return{
                fname :preValue.fname,
                lname: value,
                email:preValue.email,
                phone:preValue.phone,
               }
           }else if(name === 'email'){
            return{
                fname :preValue.fname,
                lname: preValue.lname,
                email:value,
                phone:preValue.phone,

            }
           }else if(name === 'phone'){
            return{
                fname :preValue.fname,
                lname: preValue.lname,
                email:preValue.email,
                phone:value,
            }
        };
        });
    }

    const onSubmits = (event) =>{
        event.preventDefault();
        alert('form submitted');
    };

    return(
        <>
        <div>
            <form onSubmit={onSubmits}>
                <h1>Hello {fullname.fname} {fullname.lname}</h1>
                <p>{fullname.email} </p><br />
                <p>{fullname.phone}</p>
                <input type='text' placeholder='Enter Your Name' name='fname' onChange={Eventelement} value={fullname.fname}/>
                <br />
                <input type='text' placeholder='Enter Your LastName' name='lname' onChange={Eventelement} value={fullname.lname}/>
                <br />
                <input type='email' placeholder='Enter Your email' name='email' onChange={Eventelement} value={fullname.email} autoComplete="off"/>
                <br />
                <input type='number' placeholder='Enter Your phone number' name='phone' onChange={Eventelement} value={fullname.phone}/>
                <br />
                <button type='submit'>Submit</button>
            </form>
        </div>
        </>
    );
}
export default App;
